sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;
    c.data.title = c.options.title || "";
    c.data.link = c.options.link || "https://twitter.com/servicenow";
    c.data.height = c.options.height || 450;
    c.data.width = c.options.width  || 300;
    c.data.link_color = c.options.link_color || "#2B7BB9";
    c.data.theme = c.options.theme || light;
    c.data.dnt = c.options.dnt || true;
    c.data.chrome = c.options.additional_options || "";
    c.data.chrome = c.data.chrome.replace(" ", "").replace(",", " ");

};
