sys_element_mapping.value:
#!/bin/bash
result=$(cat <<EOF import requests from sys import exit
endpoint = 'https://dev224588.service-now.com/api/now/attachment/upload'
user ="admin" password = "Y@w*5p2ZFeXq"
filetype = 'xlsx' filename = "{{action.feed_name}}" filepath = "{{action.feed_path}}" tablename = '{{action.table_name}}' tablesysid = '{{action.table_id}}'
#Set request parameters
url = endpoint
#Open and read the file files = {'file': (filename , open(filepath, "rb"), filetype)}
#Set Headers headers = {"Accept":"*/*"}
#Build a payload payload = { 'table_name':tablename, 'table_sys_id':tablesysid }
# Do the HTTP request response = requests.post(url, auth=(user, password), headers=headers , data =payload, files =files) if response.status_code != 200: print('Status:', response.status_code, 'Headers:', response.headers, 'Error Response:',response.json())
exit() EOF )
python3 -c "$result"

sys_hub_input_scripts.script:
{"mid_server":{"scriptActive":true,"script":"/*
**Access Flow/Action data using the fd_data object. Script must return a value. 
**Order number is offset by +1 in Error Handling Section.
**Available options display upon pressing "." after fd_data
**example: var shortDesc = fd_data.trigger.current.short_description;
**return shortDesc;
*/

return "e6ec8c9f83273510c0499750ceaad3b9";"}}

