sys_script_fix.script:
(function() {

	try {

		// Start Asynchronously: Uncomment to run in background. Code snippet will not have access to outputs.
		// sn_fd.FlowAPI.getRunner().action('x_1136210_new_ye_0.call_provider').inBackground().run();

		// Execute Synchronously: Run in foreground. Code snippet has access to outputs.
		var result = sn_fd.FlowAPI.getRunner().action('x_1136210_new_ye_0.call_provider').inForeground().run();
		var outputs = result.getOutputs();

		// Get Outputs:
		// Note: outputs can only be retrieved when executing synchronously.
		var variable = outputs['variable']; // Multiple Line Small Text Area
        var response=JSON.parse(variable)["result"];

         response.forEach(function(item){

            gs.addInfoMessage(item["number"]);

         })



	} catch (ex) {
		var message = ex.getMessage();
		gs.error(message);
	}

})();
